mapper.select:
select * from t_tendency

     and unid = #{unid} 
     and user_id = #{userId} 
     and group_id = #{groupId} 
     and content = #{content} 
     and status = #{status} 
     and create_date = #{createDate}

select count(*) from t_tendency

     and unid = #{unid} 
     and user_id = #{userId} 
     and group_id = #{groupId} 
     and content = #{content} 
     and status = #{status} 
     and create_date = #{createDate} 
     and create_date >= #{startDate} 
     and create_date <= #{endDate}

mapper.insert:

    SELECT REPLACE(UUID(),'-','') FROM DUAL

insert into t_tendency(
    `unid`,`user_id`,`group_id`,`content`,`status`,`create_date`
) values(
    #{unid},#{userId},#{groupId},#{content},#{status},#{createDate}
)

mapper.update:
update t_tendency
unid = #{unid}, user_id = #{userId}, group_id = #{groupId}, content = #{content}, status = #{status}, create_date = #{createDate},
where unid = #{unid}

